<template>
    <div class="profiles__item-1">
        <div class="profiles-action">

            <router-link :to="{name: 'TotalAnalytics'}" tag="div" class="start-new-project__item start-new-project__analytics" v-if="getActiveSurveys.length || getCompletedSurveys.length">
                <div class="action-item__icon">
                    <img width="57" height="57" src="data:image/svg+xml;utf8;base64," />
                </div>
                <div class="start-new-project__title" v-lang.analytics></div>
                <div class="start-new-project__desc relative">
                    <span v-lang.analyticsComment></span>

                    <transition name="hide">
                        <div ref="tooltip" v-if="$store.getters.showAnalyticsHint" class="tooltip-completed-hint show" @click.stop="">
                            <div class="tooltip-content__arrow-top"></div>
                            <span v-lang.analyticsHint></span>
                        </div>
                    </transition>
                </div>
            </router-link>

            <router-link :to="{name: 'Employees'}" tag="div" class="profiles-action__col">
                <a>
                    <div class="action-item">
                        <div class="action-item__icon">
                            <img class="action-item__img-1" src="../../assets/img/action-1.svg" width="64px" height="55px">
                        </div>
                        <div class="action-item__title" v-lang.employeesData></div>
                        <div class="action-item__desc" v-lang.indicators></div>
                    </div>
                </a>
            </router-link>

            <router-link :to="{name: 'CreateSurvey'}" tag="div" class="profiles-action__col">
                <a>
                    <div class="action-item">
                        <div class="action-item__icon">
                            <img class="action-item__img-2" src="../../assets/img/action-2.svg" width="55px" height="55px">
                        </div>
                        <div class="action-item__title" v-lang.start></div>
                        <div class="action-item__desc" v-lang.startLaunch></div>
                    </div>
                </a>
            </router-link>

            <div class="profiles-action__col hide">
                <div class="action-item">
                    <div class="action-item__icon">
                        <img class="action-item__img-2" src="../../assets/img/action-3.svg" width="55px" height="55px">
                    </div>
                    <div class="action-item__title" v-lang.reports></div>
                    <div class="action-item__desc" v-lang.generate></div>
                </div>
            </div>

            <div class="profiles-action__col hide">
                <div class="action-item">
                    <div class="action-item__icon">
                        <div v-if="recommendationsCount > 0" class="action-item__message" v-text="recommendationsCount"></div>
                        <img class="action-item__img-2" src="../../assets/img/action-4.svg" width="55px" height="55px">
                    </div>
                    <div class="action-item__title" v-lang.recommendations></div>
                    <div class="action-item__desc" v-lang.recommendationsSystem></div>
                </div>
            </div>
        </div>
    </div>
</template>

<script>
    import { mapGetters } from 'vuex';

    export default {
        props: {
            recommendationsCount: {
                type: Number,
                default: 0
            }
        },
        created() {
            this.$bus.$on('documentClick', this.hideHint);
        },
        beforeDestroy() {
            this.$bus.$off('documentClick', this.hideHint);
        },
        methods: {
            hideHint() {
                if (!this.$refs.tooltip || !this.$store.getters.showAnalyticsHint) {
                    return;
                }
                this.$store.dispatch('removeAnalyticsHint');
            }
        },
        computed: {
            ...mapGetters('survey', [
                'getCompletedSurveys',
                'getActiveSurveys'
            ])
        }
    };
</script>

<style lang="less" rel="stylesheet/less">
    .show {
        opacity: 1;
        visibility: visible;
    }

    .hide-enter, .hide-leave-to {
        opacity: 0;
        visibility: hidden;
    }

    .hide-enter-active, .hide-leave-active {
        transition: all .5s;
    }
</style>